import { Component, OnInit } from '@angular/core';
import { Products } from 'src/app/products';

@Component({
  selector: 'app-slider',
  templateUrl: './slider.component.html',
  styleUrls: ['./slider.component.css']
})
export class SliderComponent implements OnInit {
  products: Products[] = [
    {
      imagem: 'assets/products/banner1.png',
      nome: 'Blazer Masculino',
      descricao: 'New Arrival',
      preco: '289.45'
    },
    {
      imagem: 'assets/products/banner2.png',
      nome: 'Vestido Cetin Roxo',
      descricao: 'New Arrival',
      preco: '3.279'
    },
    {
      imagem: 'assets/products/banner3.png',
      nome: 'Tênis Corrida',
      descricao: 'New Arrival',
      preco: '119.00'
    },
    {
      imagem: 'assets/products/banner4.png',
      nome: 'Smart Watch',
      descricao: 'New Arrival',
      preco: '120.00'
    },
    {
      imagem: 'assets/products/delta.png',
      nome: 'Bota Motociclista',
      descricao: 'New Arrival',
      preco: '875.00'
    },
    {
      imagem: 'assets/products/feature_3.png',
      nome: 'Tênis Corrida',
      descricao: 'Alta Performance',
      preco: '429.00'
    },
    {
      imagem: 'assets/products/image2.png',
      nome: 'Sandália Anabella',
      descricao: 'New Arrival',
      preco: '119.00'
    },
    {
      imagem: 'assets/products/image3.png',
      nome: 'Tenis 12 Molas',
      descricao: 'New Arrival',
      preco: '475.00'
    },
    {
      imagem: 'assets/products/img08.jpg',
      nome: 'Ração Para Gatos',
      descricao: 'New Arrival',
      preco: '27.00'
    },
    {
      imagem: 'assets/products/img09.jpg',
      nome: 'Vermífugo',
      descricao: 'New Arrival',
      preco: '14.00'
    }
  ];

  l = 0;
  movePer = 25.34;
  maxMove = 203;
  product_page = Math.ceil(this.products.length / 4);

  constructor() { }


  ngOnInit(): void {
    // mobil.view
    const mob_view = window.matchMedia('(max-width: 768px)');
    if (mob_view.matches) {
      this.movePer = 50.36;
      this.maxMove = 504;
    }
  }

  right_mover() {
    this.l = this.l + this.movePer;
    if (this.l > this.maxMove) {
      this.l = this.l - this.movePer;
    }

    const productContainer = document.getElementById('productContainer');
    if (productContainer) {
      productContainer.style.left = '-' + this.l + '%';
    }
  }

  left_mover() {
    this.l = this.l - this.movePer;
    if (this.l <= 0) {
      this.l = 0;
    }

    const productContainer = document.getElementById('productContainer');
    if (productContainer) {
      productContainer.style.left = '-' + this.l + '%';
    }
  }






}
